$(function() {
    $("#close_im").click(function() {
        $("#main-im").css("height", "0");
        $("#im_main").hide();
        $("#open_im").show()
    });
    $("#open_im").click(function(e) {
        $("#main-im").css("height", "272");
        $("#im_main").show();
        $(this).hide()
    });
    $(".go-top").click(function() { $(window).scrollTop(0) });
    $(".weixing-container").mouseenter(function() { $(".weixing-show").show() });
    $(".weixing-container").mouseleave(function() { $(".weixing-show").hide() })
});

(function() {
    var one = $('.to_one'),
        two = $('.to_two'),
        three = $('.to_three');
    one.click(function() { scrollTo($('#zl3')) });
    two.click(function() { scrollTo($('#zl7')) });
    three.click(function() { scrollTo($('#zl12')) });

    function scrollTo(target) {
        var target_top = target.offset().top;
        $("html,body").animate({ scrollTop: target_top }, 500);
    }
}());