$(function() {
    $(window).scroll(scrolls);
    scrolls();

    function scrolls() {
        var zl1, zl2, zl3, zl4, zl5, zl6, zl7, zl8, zl9, zl10, zl11, zl12, sTop = $(window).scrollTop(),
            zl1 = $("#zl1").offset().top;
        zl2 = $("#zl2").offset().top;
        zl3 = $("#zl3").offset().top;
        zl4 = $("#zl4").offset().top;
        zl5 = $("#zl5").offset().top;
        zl6 = $("#zl6").offset().top;
        zl7 = $("#zl7").offset().top;
        zl8 = $("#zl8").offset().top;
        zl9 = $("#zl9").offset().top;
        zl10 = $("#zl10").offset().top;
        zl11 = $("#zl11").offset().top;
        zl12 = $("#zl12").offset().top;
        if (sTop >= zl1 - 300) { $("#zl1").addClass("cur") }
        if (sTop >= zl2 - 300) { $("#zl2").addClass("cur") }
        if (sTop >= zl3 - 300) { $("#zl3").addClass("cur") }
        if (sTop >= zl4 - 300) { $("#zl4").addClass("cur") }
        if (sTop >= zl5 - 300) { $("#zl5").addClass("cur") }
        if (sTop >= zl6 - 300) { $("#zl6").addClass("cur") }
        if (sTop >= zl7 - 300) { $("#zl7").addClass("cur") }
        if (sTop >= zl8 - 300) { $("#zl8").addClass("cur") }
        if (sTop >= zl9 - 300) { $("#zl9").addClass("cur") }
        if (sTop >= zl10 - 300) { $("#zl10").addClass("cur") }
        if (sTop >= zl11 - 300) { $("#zl11").addClass("cur") }
    }
});
